let myClip;

const Tester = new TesterClass();

/** ************************* PLUGIN DEFINITION ************************ */
/** ******************************************************************* */
class MyPluginIncident extends API.MonoIncident {
  onGetContext() {
    // console.log('got context');
  }

  onProgress(fraction) {
    // eslint-disable-line no-unused-vars
    const { initialValue } = this;
    const { targetValue } = this;
    const value = initialValue + (targetValue - initialValue) * fraction;
    // console.log(`initialValue of ${this.id}: ${initialValue}`);
    // console.log(`setting value to ${value} for ms = ${ms}`);
    this.element.setAttribute(this.attributeKey, value);
  }

  getScratchValue() {
    // eslint-disable-line no-unused-vars
    if (this.attributeKey === 'alpha') {
      return 1;
    }
    if (this.attributeKey === 'beta') {
      return 2.5;
    }
    if (this.attributeKey === 'compo') {
      return {
        gamma: 3,
        delta: 4,
        epsilon: 5
      };
    }

    return 0;
  }
}

/** ************************* PLUGIN LOADING ************************** */
/** ******************************************************************* */
const testPlugin0 = {
  npm_name: '@kissmybutton/test-plugin-0',
  name: 'Test Plugin 0',
  incidents: [
    {
      exportable: MyPluginIncident,
      name: 'MPInc'
    }
  ],
  compositeAttributes: {
    compo: ['gamma', 'delta', 'epsilon']
  }
};

const TestPlugin = loadPlugin(testPlugin0);

/** ****************************** INCIDENTS ************************** */
/** ******************************************************************* */
let incident1;
let cincident1;
let incident2;
let cincident2;
let incident3;
let cincident3;
let incident4;
let cincident4;

test('IV1. Check Incident with initial values on some of its plain attributes', () => {
  document.body.innerHTML = '<div id="clip-container"></div>';

  myClip = new Clip({
    html: `
            <div id="my-root-div">
                <div id="element-1" class="my-class" data-motorcortex2-id="my-class-1"></div>
                <div id="element-2" class="my-class" data-motorcortex2-id="my-class-2"></div>
            </div>`,
    css: `
            #my-root-div {
                width: 800px;
                height: 400px;
            }
            .my-class {
                display: inline-block;
                width: 50%;
                height: 100%;
            }
        `,
    host: document.querySelector('#clip-container'),
    id: 'my-clip'
  });

  incident1 = new TestPlugin.MPInc(
    {
      animatedAttrs: {
        alpha: 2,
        beta: 2
      },
      initialValues: {
        alpha: 3
      }
    },
    {
      selector: '#element-1',
      duration: 1000,
      id: 'incident1'
    }
  );

  const res = myClip.addIncident(incident1, 3000);
  expect(res.result).toBe(true);

  const lh = myClip.realClip.instantiatedChannels['@kissmybutton/test-plugin-0'].LanesHandler;
  const lanesTest = Tester.testLanes(lanes1, lh.lanes, lh.incidentsById);
  expect(lanesTest).toHaveNoErros();
});

